package loops;

public class l1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		
		int i=6;
		do {
			System.out.println(i);
			i=i+6;
		}
		while(i<=60);
	
		int a=77;
		do {
			System.out.println(a);
			a=a-2;
		}
		while(a>=7);
	
		int f=1;
		do {
			System.out.println("Reshu");
			f=f+1;
		}
		while(f<=10);
		
		
		}
	}


